sys_ui_action.script:
//Create New Querry to Find Attachment
var sa = newGlideRecord('sys_attachment'); 
sa.addQuery('table_name','initial requests');
sa.addQuery('sys_id',current.sys_id);
sa.addQuery();

var b64attachment = '';

if( sa.next() ) {
	var attachmentIS = new GlideSysAttachmentInputStream(sa.sys_id);
	var bytearrayOS = new Packages.java.io.ByteArrayOutputStream();

	//Write byte stream
	attachmentIS.writeTo(bytearrayOS);
	//encode attachment file
	b64attachment = GlideBase64.encode(bytearrayOS,toByteArray());

	//Create REST Request
	var request = new sn_ws.RESTMessageV2();
	request.setHttpMethod('post');
	request.setEndpoint("https://api.ocr.space/parse/image");

	var body = ("apikey", "c2e469bf8288957");
	body.append("language", "eng");
	body.append("isOverlayRequired", "true");
	body.append("filetype", "application/pdf");
	body.append("base64image", "data:application/pdf;base64" + b64attachment );
	gs.print(body);

	request.setRequestBody(body);

var response = request.execute();
var httpResponseStatus = response.getStatusCode();
gs.print("response is" + response);
}
